import React from 'react';
import { Card, CardContent, Typography, Box, Table, TableContainer, TableHead, TableRow, TableCell, TableBody } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

const ResultsComponent = ({ results }) => {
  if (!results) return null;

  const { data, meta } = results;

  if (!data || !data.attributes || !data.attributes.stats) return null;

  const { stats } = data.attributes;

  // Convert stats object into an array of objects for easier mapping
  const statsArray = [
    { category: 'Harmless', count: stats.harmless },
    { category: 'Malicious', count: stats.malicious },
    { category: 'Suspicious', count: stats.suspicious },
    { category: 'Timeout', count: stats.timeout },
    { category: 'Undetected', count: stats.undetected }
  ];

  // Referencia para controlar el elemento expandido
  const expandableRef = React.useRef(null);

  const toggleExpanded = () => {
    const content = expandableRef.current;
    if (content.style.display === 'none') {
      content.style.display = 'block';
    } else {
      content.style.display = 'none';
    }
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Card>
        <CardContent>
          <Typography variant="h5" component="h2">
            Analysis Results
          </Typography>
          <TableContainer sx={{ mt: 2 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Category</TableCell>
                  <TableCell align="right">Count</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {statsArray.map((stat, index) => (
                  <TableRow key={index}>
                    <TableCell>{stat.category}</TableCell>
                    <TableCell align="right">{stat.count}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>
      </Card>

      <Box sx={{ mt: 4 }}>
        <Card>
          <CardContent>
            <Typography variant="h5" component="h2">
              Analysis Results
            </Typography>
            <Typography variant="body2" component="pre" sx={{ mt: 2 }} onClick={toggleExpanded} style={{ cursor: 'pointer' }}>
              Click aquí para ver más detalles
            </Typography>
            <Typography variant="body2" component="pre" sx={{ mt: 2 }} ref={expandableRef} style={{ display: 'none' }}>
              {JSON.stringify(data, null, 2)}
            </Typography>
            <Typography variant="body2" component="pre" sx={{ mt: 2 }} ref={expandableRef} style={{ display: 'none' }}>
              {JSON.stringify(meta, null, 2)}
            </Typography>
          </CardContent>
        </Card>
      </Box>
    </Box>
  );
};

export default ResultsComponent;
