import React from 'react';
import { Card, CardContent, Typography, Box, Table, TableContainer, TableHead, TableRow, TableCell, TableBody, Button, Collapse } from '@mui/material';

const ResultsComponent = ({ results }) => {
  if (!results) return null;

  const { data, meta } = results;

  if (!data || !data.attributes || !data.attributes.stats) return null;

  const { stats } = data.attributes;

  // Convert stats object into an array of objects for easier mapping
  const statsArray = [
    { category: 'Harmless', count: stats.harmless },
    { category: 'Malicious', count: stats.malicious },
    { category: 'Suspicious', count: stats.suspicious },
    { category: 'Timeout', count: stats.timeout },
    { category: 'Undetected', count: stats.undetected }
  ];

  // Variable to toggle full JSON visibility
  let showFullJson = false;

  const toggleShowFullJson = () => {
    showFullJson = !showFullJson;
    forceUpdate();
  };

  // Helper function to force re-render
  const [, forceUpdate] = React.useReducer(x => x + 1, 0);

  return (
    <Box sx={{ mt: 4 }}>
      <Card>
        <CardContent>
          <Typography variant="h5" component="h2">
            Analysis Results (Summary)
          </Typography>
          <TableContainer sx={{ mt: 2 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Category</TableCell>
                  <TableCell align="right">Count</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {statsArray.map((stat, index) => (
                  <TableRow key={index}>
                    <TableCell>{stat.category}</TableCell>
                    <TableCell align="right">{stat.count}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>
      </Card>

      <Box sx={{ mt: 4 }}>
        <Card>
          <CardContent>
            <Typography variant="h5" component="h2">
              Full Analysis Results
            </Typography>
            <Button onClick={toggleShowFullJson} variant="outlined" sx={{ mt: 2 }}>
              {showFullJson ? 'Hide Full JSON' : 'Show Full JSON'}
            </Button>
            {showFullJson && (
              <>
                <Typography variant="body2" component="pre" sx={{ mt: 2 }}>
                  {JSON.stringify(data, null, 2)}
                </Typography>
                <Typography variant="body2" component="pre" sx={{ mt: 2 }}>
                  {JSON.stringify(meta, null, 2)}
                </Typography>
              </>
            )}
          </CardContent>
        </Card>
      </Box>
    </Box>
  );
};

export default ResultsComponent;
